import axios from "axios";

export const sign_in_async = (email, password) => {
  return (dispatch) => {
    const query = `query{
            login(email:"${email}" password:"${password}"  ){
                token
            }
          }`;
    axios({
      url: "http://localhost:5000/graphql",
      method: "POST",
      data: {
        query: query,
      },
    }).then((res) => {
        dispatch()

      console.log(res);
    });
  };
};
