import React, { useState } from "react";
import EditorComponent from "../../components/editor/editor";
import { EditorState ,convertFromRaw, convertToRaw   } from 'draft-js';
import draftToHtml from 'draftjs-to-html';
import axios from "axios"
import "./create-post.scss";
import { connect } from "react-redux";
import { tokenSelector } from "../../redux/auth/auth.selector";
function CreatePost({token}) {
//   const [editorState, updateeditorState] = useState(EditorState.createEmpty());
    const [postForm ,updatePostForm ] = useState({title:"", content:""})
    const [editorState , upadateEditorState]= useState()
    const onEditorStateChange = (editorState=>{
        const rawContentState = convertToRaw(editorState.getCurrentContent());     
        const HtmlContent = draftToHtml(rawContentState);
        upadateEditorState(HtmlContent)
        console.log(editorState);
      })

    const { title , content } = postForm
    const formOnChange = (event)=>{
        const { name , value}= event.target
        updatePostForm(preval =>{
            return {...preval , [name] : value}
        })
    }
    const formOnSubmit = (event)=>{
        event.preventDefault()
        const query = `mutation{
            createPost(title:"${title}" content:"${editorState}" ){
              _id
              title
              content
              postCreator{
                  _id
                  displayName
              }
              relatedComment{
                  commentContent
              }

            }
          }`;
          axios({
            url: "http://localhost:5000/graphql",
            method: "POST",
            data: {
              query: query,
            },
            headers: {
              "Content-Type": "application/json",
              Authorization: "Bearer " + token,
            },
          })
            .then((res) => {
              console.log(res);
            })
            .catch((err) => {
                console.log(err);
            });

    }
  return (
    <div className="create_post">
      <div className="container">
        <form className="create_post_form" onSubmit={formOnSubmit} > 
          <label htmlFor="title"> title </label>
          <input type="text" value={title} name="title"  onChange={formOnChange} />
          <label htmlFor="title"> post content </label>
          {/* <textarea name="content" value={content} id="" cols="30" rows="10" onChange={formOnChange} ></textarea> */}
          <EditorComponent
            className="editor"
            onEditorStateChange={onEditorStateChange}
          />
          <button>Add post</button>
        </form>
      </div>
    </div>
  );
}
const mapStateToProps = (state, ownprops) => ({
    token: tokenSelector(state),
  });
export default connect(mapStateToProps)( CreatePost);
