import React from "react";
import "./postpage.scss";
import CommentIcon from "@material-ui/icons/Comment";
import DateRangeIcon from "@material-ui/icons/DateRange";
import { useState } from "react";
import { connect } from "react-redux";
import { selectPostById } from "../../redux/posts/post.selector";
import { createStructuredSelector } from "reselect";
import Post from "../../components/post/post"
function Postpage({post}) {
  console.log(post);

  return (
    <div className="postpage">
      {
        <Post  key={post._id}
        _id ={post._id}
        title={post.title}
        Content={post.content.slice(0 , 160)}
        date={post.date}
        writer={post.postCreator.displayName}
        comments={post.relatedComment.length}  />
      }

    </div>
  );
}
const mapStateToProps = (state, ownprops) => ({
  post: selectPostById(ownprops.match.params.postId)(state),
  });

export default  connect(mapStateToProps)(Postpage)
