import React, { useState, useEffect } from "react";
import Post from "../post/post";
import { postsSelector ,isFitchingSelector } from "../../redux/posts/post.selector";
import { createStructuredSelector } from "reselect";
import { connect } from "react-redux";
function PostCollection({ posts , isFitching }) {
  const [Posts, updatePosts] = useState([]);

  useEffect(() => {
    updatePosts(posts);
  }, [isFitching]);

  return (
    <div className="post-collection">
      {Posts.map((post) => {
        return (
          <Post
            key={post._id}
            title={post.title}
            Content={post.content}
            date={post.date}
            writer={post.postCreator.displayName}
            comments={post.relatedComment.length}
          />
        );
      })}
    </div>
  );
}
const mapStateToProps = createStructuredSelector({
  posts: postsSelector,
  isFitching : isFitchingSelector
});
export default connect(mapStateToProps)(PostCollection);
