import auth_types from "./auth.types";
const INTIAL_STATE = {
  token: null,
  userId: null,
  email: null,
  displayName: null,
  tokenExpriration: null,
  auth_state : false
};
const authReducer = (state = INTIAL_STATE, action) => {
  switch (action.type) {
    case auth_types.SIGN_IN_SUCCESS:
      return {
        ...state,
        ...action.payload,
        auth_state:true
      };

      case auth_types.SIGN_IN_FAIL:
          return {
              ...state , 
              auth_state: false

          }
    case auth_types.SIGN_OUT:
      return {
        ...state,
        ...INTIAL_STATE,
        auth_state: false
      };
    default:
      return state;
  }
};
export default authReducer
