import EditorComponent from "../../components/editor/editor";
import { useState } from "react";
import WithSpinner from "../../components/withspiner.component/withspiner.component";
import { connect } from "mongoose";
import { isFitchingSelector } from "../../redux/posts/post.selector";
import { createStructuredSelector } from "reselect";
function Postpage() {
  const [texteditor, updateTextEditor] = useState("");

  const onChange = (event) => {
    console.log(event);
  };
  return (
    <div className="postpage">
      <div className="container">
        <h1 className="title">
          The Mythbusterâ€™s Guide to Saving Money on Energy Bills
        </h1>

        <div className="sub_info">
          <div className="writer">
            <img
              src="https://static.toiimg.com/thumb/72975551.cms?width=680&height=512&imgsize=881753"
              alt=""
            />
            <strong>writer</strong>
          </div>
          <p>
            {" "}
            <CommentIcon
              style={{ marginRight: "10px", cursor: "pointer" }}
            />{" "}
          </p>

          <p>
            {" "}
            <DateRangeIcon style={{ marginRight: "10px" }} />{" "}
          </p>
        </div>

        <div className="image-container">
          <img
            src="https://static.toiimg.com/thumb/72975551.cms?width=680&height=512&imgsize=881753"
            alt=""
          />
        </div>

        <div className="content">
          <EditorComponent editorContent={onChange} />
        </div>
      </div>
    </div>
  );
}
const mapStateToProps = createStructuredSelector({
  isFitching: isFitchingSelector,
});

export default connect(mapStateToProps)(WithSpinner(Postpage));
