import React from "react";
import "./postpage.scss";
import CommentIcon from "@material-ui/icons/Comment";
import DateRangeIcon from "@material-ui/icons/DateRange";
import { useState } from "react";
import { connect } from "react-redux";
import { selectPostById } from "../../redux/posts/post.selector";
import { createStructuredSelector } from "reselect";
import Comment from "../../components/comment/comment"

import Post from "../../components/post/post";
function Postpage({ post }) {
  console.log(post);
  return (
    <div className="postpage">
      <div className="container">
        {post && (
          <Post
            title={post.title}
            Content={post.content}
            date={post.date}
            writer={post.postCreator.displayName}
          />
        )}

        {/* <div>
          {" "}
          {post.relatedComment && (
            <div>
              <h2 className="commentb"> Comments</h2>
              {post.relatedComment?.map((comment) => {
                return (
                  <Comment
                    commentContent={post.comment.commentContent}
                    creator={post.comment.commentCreator}
                  />
                );
              })}
            </div>
          )}{" "}
        </div> */}
      </div>
    </div>
  );
}
const mapStateToProps = (state, ownprops) => ({
  post: selectPostById(ownprops.match.params.postId)(state),
});

export default connect(mapStateToProps)(Postpage);
