import React ,{ useState,useEffect } from 'react'
import Post from '../post/post'
import axios from "axios"
import {postsSelector} from "../../redux/posts/post.selector"
import { createStructuredSelector} from "reselect"
import { connect } from 'mongoose'
function PostCollection({posts}) {

  const [Posts , updatePosts]= useState([])




      useEffect(()=>{

          updatePosts()

      },[])

    return (
        <div className="post-collection">
              {Posts?.map(post=>{

              return <Post key={post._id}   title={post.title}  Content={post.content} date ={post.date}  writer={post.postCreator.displayName}  comments={post.relatedComment.length} />

              })}




        </div>
    )
}
const mapStateToProps = createStructuredSelector({
  posts : postsSelector
})
export default connect(mapStateToProps)( PostCollection)
