import React, { useState } from "react";
import EditorComponent from "../../components/editor/editor";
import { EditorState, convertToRaw, convertFromHTML } from "draft-js";
import axios from "axios";
import "./create-post.scss";
import { connect } from "react-redux";
import { tokenSelector } from "../../redux/auth/auth.selector";
import { addPost } from "../../redux/posts/post.action";
function CreatePost({ token, dispatch }) {
  const [editorState, updateeditorState] = useState();
  const [postForm, updatePostForm] = useState({ title: "", content: "" });
  const onEditorStateChange = (EditorState) => {
    const rawContentState = convertToRaw(EditorState.getCurrentContent());
    updateeditorState(JSON.stringify(rawContentState));
    console.log(editorState);
  };

  const { title, content } = postForm;
  const formOnChange = (event) => {
    const { name, value } = event.target;
    updatePostForm((preval) => {
      return { ...preval, [name]: value };
    });
  };
  const formOnSubmit = (event) => {

    event.preventDefault();
    const content =  editorState
    const query = `mutation createPost($title: String! , $content: String!) {
            createPost(title: $title ,  content: $content ){
                _id
                title
                content
                date
                relatedComment{
                  commentContent
                  commentCreator{
                    displayName
                  }
                }
                postCreator{
                  displayName
                }


            }
          }`;
    axios({
      url: "http://localhost:5000/graphql",
      method: "POST",
      data: JSON.stringify({
        query,
        variables: { content, title },
      }),

      headers: {
        "Content-Type": "application/json",
        Authorization: "Bearer " + token,
      },
    })
      .then((res) => {
        console.log(res);
        dispatch(addPost(res.data.data.createPost));
      })
      .catch((err) => {
        console.log(err);
      });
  };
  return (
    <div className="create_post">
      <div className="container">
        <form className="create_post_form" onSubmit={formOnSubmit}>
          <label htmlFor="title"> title </label>
          <input
            type="text"
            value={title}
            name="title"
            onChange={formOnChange}
          />
          <label htmlFor="title"> post content </label>
          <textarea
            name="content"
            value={content}
            id=""
            cols="30"
            rows="10"
            onChange={formOnChange}
          ></textarea>
          <EditorComponent
            className="editor"
            onEditorStateChange={onEditorStateChange}
          />
          <button>Add post</button>
        </form>
      </div>
    </div>
  );
}
const mapStateToProps = (state, ownprops) => ({
  token: tokenSelector(state),
});
export default connect(mapStateToProps)(CreatePost);
